var elementOffset = 500;$('document').ready(function(){$(window).on('scroll',function(){var toFade = document.getElementsByClassName('fade-jr');for (var i = 0; i < toFade.length; i++) {if( $(window).scrollTop() > ($('.fade-jr').eq(i).offset().top-elementOffset)){$('.fade-jr').eq(i).addClass('zoom');$('.fade-jr').eq(i).css("opacity","1");}}});});